# iterate over all files within "Engineering Test Files"
for file in os.listdir(os.path.expanduser("~/Desktop/Engineering Test Files/")):
    if file.endswith(".csv"):
        tmp = pd.read_csv(os.path.join("~/Desktop/Engineering Test Files/",file))
        tmp.to_csv("combined.csv", index=True, header=True, mode='a') #combined file created
